//custom scroll init
function initCustomScroll(){
	var isTouchDevice = (/MSIE 10.*Touch/.test(navigator.userAgent)) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	var win = jQuery(window);
	var minRows = 6;
	var sortClass = 'sorting-active';
	if(isTouchDevice) jQuery('body').addClass('touch-device');
	jQuery('.dashboard-drop').each(function(){
		var holder = jQuery(this).removeClass('custom');
		var lists = holder.find('.menu');
		var scrollBox = holder.find('.scrollable-area');
		var leftArrow = jQuery('<div class="hscroll-left">');
		var rightArrow = jQuery('<div class="hscroll-right">');
		leftArrow.appendTo(holder).click(function(){
			var currentPosition = scrollBox.scrollLeft();
			scrollBox.stop().animate({scrollLeft: currentPosition - 120});
		});
		rightArrow.appendTo(holder).click(function(){
			var currentPosition = scrollBox.scrollLeft();
			scrollBox.stop().animate({scrollLeft: currentPosition + 120});
		});
		function setListWidth(){
			lists.each(function(){
				var list = jQuery(this);
				var totalWidth = 0;
				list.children().each(function(){
					totalWidth += jQuery(this).outerWidth(true);
				});
				list.css({width: totalWidth + 20});
			});
		}
		lists.sortable({
			delay: isTouchDevice ? 2000 : 0,
			connectWith: '.menu',
			start:function(){
				lists.css({width: 9999999});
				holder.addClass(sortClass);
			},
			stop:function(){
				setListWidth();
				holder.removeClass(sortClass);
			}
		}).disableSelection();
		setListWidth();
		
		if(isTouchDevice){
			var originalOffset;
			scrollBox.hammer({
				drag_block_vertical: true,
				drag_min_distance: 1
			}).on('drag dragstart', function(ev){
				switch(ev.type) {
					case 'dragstart':
						originalOffset = scrollBox.scrollLeft();
						return false;
						break;
					case 'drag':
						if(!holder.hasClass(sortClass)) scrollBox.scrollLeft(originalOffset - ev.gesture.deltaX*(jQuery('html.chrome').length ? 10 : 1))
						break;
				}
			});
		} else {
			if(jQuery.browser.mozilla){
				scrollBox.bind('DOMMouseScroll', function(e){
					var currentPosition = scrollBox.scrollLeft();
					scrollBox.scrollLeft(currentPosition + e.originalEvent.detail*20)
					return false;
				});
			} else {
				scrollBox.bind('mousewheel', function(e){
					var currentPosition = scrollBox.scrollLeft();
					scrollBox.scrollLeft(currentPosition - e.originalEvent.wheelDeltaY)
					return false;
				});
			}
		}
	})
	
	function resizeScrollSection(parent,holder){
		function bindResize(){
			holder.css({width: parent.width()});
			if(holder.get(0).jcf){
				holder.parent().css({width:''});
				holder.get(0).jcf.refreshState();
			}
		}
		win.bind('load resize orientationchange',bindResize);
	}
	
	jQuery('table.scroll-table').each(function(){
		var table = jQuery(this);
		var tbody = table.find('tbody');
		if(tbody.find('tr').length < minRows) return;
		var numCells = table.data('colspan') ? parseInt(table.data('colspan')) : 1;
		var cloneTable = table.clone();
		if(cloneTable.find('thead').length){
			cloneTable.find('thead').remove();
		}
		var row = jQuery('<tr/>');
		var cell = jQuery('<td class="union-cell" colspan="'+ numCells +'"/>');
		var holder = jQuery('<div class="scrollable-area"/>');
		
		holder.css({width: table.width()})
		
		holder.append(cloneTable);
		tbody.empty();
		cell.append(holder);
		row.append(cell);
		tbody.append(row);
		resizeScrollSection(table.parent(),holder);
	})
}

//add email address init
function initAddEmail(){
	var activeClass = 'active';
	jQuery('.forms-holder').each(function(){
		var holder = jQuery(this);
		var addForm = holder.find('.get-email');
		var setEmailHolder = holder.find('.set-email');
		if(addForm.length && setEmailHolder.length){
			var inputEmail = addForm.find('input[type="email"]');
			var listEmails = setEmailHolder.find('.text-container');
			var btnDel = setEmailHolder.find('.btn-del');
			listEmails.empty();
			function addEvents(item){
				item.bind('click',function(e){
					e.preventDefault();
					item.siblings().removeClass(activeClass);
					if(!item.hasClass(activeClass)) item.addClass(activeClass);
					else item.removeClass(activeClass);
				})
			}
			function removeItem(){
				if(listEmails.find('.' + activeClass).length){
					listEmails.find('.' + activeClass).remove();
				}
			}
			function addEmail(){
				var li = jQuery('<li>');
				var link = jQuery('<a>');
				link.text(inputEmail.val());
				li.append(link).appendTo(listEmails);
				addEvents(li);
				inputEmail.val('');
				setEmailHolder.show();
			}
			btnDel.bind('click',function(e){
				e.preventDefault();
				removeItem();
			})
			addForm.bind('submit',function(e){
				e.preventDefault();
				addEmail();
			})
		}
	})
}

// open-close init
function initOpenClose() {
	var classNav = 'nav-active';
	var classDrop = 'drop-active';
	var win = jQuery(window);
	var isTouchDevice = (/MSIE 10.*Touch/.test(navigator.userAgent)) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	jQuery('.navbar').openClose({
		activeClass: 'active',
		opener: '.btn-dashboard',
		slider: '.dashboard-drop',
		animEnd: function(direction,that){
			if(isTouchDevice){
				if(direction) {
					win.trigger('resize');
					that.holder.addClass(classDrop).css({
						position: 'absolute',
						top: win.scrollTop()
					})
				}
				else{
					if(!that.holder.hasClass(classNav)){
						that.holder.removeAttr('style');
					}
					that.holder.removeClass(classDrop);
				}
			}
			else{
				if(direction) that.holder.addClass(classDrop);
				else that.holder.removeClass(classDrop);
			}
			
		},
		animSpeed: 400,
		effect: 'slide'
	});
	jQuery('#header').openClose({
		activeClass: 'active',
		opener: '.navbar-toggle',
		slider: '.navigation',
		onInit: function(that){
			// handle layout resize
			ResponsiveHelper.addRange({
				'..767': {
					on: function() {
						if(!that.holder.hasClass(that.options.activeClass)){
							that.slider.addClass('js-slide-hidden');
						}
						else{
							that.slider.removeClass('js-slide-hidden');
						}
					}
				},
				'768..': {
					on: function() {
						that.slider.removeClass('js-slide-hidden').css({display:'block'});
					}
				}
			});
		},
		animEnd: function(direction,that){
			if(isTouchDevice){
				if(direction) {
					if(that.holder.parents('.navbar').length){
						that.holder.parents('.navbar').eq(0).addClass(classNav).css({
							position: 'absolute',
							top: win.scrollTop()
						})
					}
				}
				else{
					if(that.holder.parents('.navbar').length){
						if(!that.holder.parents('.navbar').eq(0).hasClass(classDrop)) that.holder.parents('.navbar').eq(0).removeAttr('style');
						that.holder.parents('.navbar').eq(0).removeClass(classNav);
					}
				}
			}
		},
		animSpeed: 400,
		effect: 'slide'
	});
}
